mapper.select:
SELECT * FROM t_problem_ratio
WHERE contest_id = #{contestId}
AND exam_id = #{examId}
AND problem_id = #{problemId}

mapper.update:
UPDATE t_problem_ratio
SET submit_time = #{submitTime},
ac_time = #{acTime},
ratio_value = #{ratioValue}
WHERE problem_id = #{idParam.problemId}
AND contest_id = #{idParam.contestId}
AND exam_id = #{idParam.examId}

mapper.delete:
DELETE FROM t_problem_ratio
WHERE

    (
    contest_id = #{idParam.contestId}
    AND exam_id = #{idParam.examId}
    AND problem_id = #{idParam.problemId}
    )
