mapper.select:
SELECT * FROM t_user_solve
WHERE contest_id = #{contestId}
AND exam_id = #{examId}
AND user_id = #{userId}
AND problem_id = #{problemId}

mapper.update:
UPDATE t_user_solve
SET solve_state = #{solveState}
WHERE user_id = #{idParam.userId}
AND contest_id = #{idParam.contestId}
AND exam_id = #{idParam.examId}
AND problem_id = #{idParam.problemId}

mapper.delete:
DELETE FROM t_user_solve
WHERE

    (
    contest_id = #{idParam.contestId}
    AND exam_id = #{idParam.examId}
    AND user_id = #{idParam.userId}
    AND problem_id = #{idParam.problemId}
    )
